-- ============================================
-- TC-06: Kafka Connector
-- ============================================
-- Prerequisites: Kafka is running with test-1 topic
-- containing messages: {id:1,name:Hello}, {id:2,name:Test}, {id:3,name:Bye}
--
-- STEP 1: Execute this SQL, then run the job
-- EXPECTED: Job starts, status is RUNNING
-- ============================================

DROP TABLE IF EXISTS kafka_1;
DROP TABLE IF EXISTS print_sink_1;

CREATE TABLE `kafka_1` (
  `id` INT,
  `name` STRING
) WITH (
  'connector' = 'kafka',
  'topic' = 'test-1',
  'properties.bootstrap.servers' = 'csa-qe-kafka:9092',
  'properties.security.protocol' = 'PLAINTEXT',
  'scan.startup.mode' = 'earliest-offset',
  'format' = 'json'
);

CREATE TABLE `print_sink_1` (
  `id` INT,
  `name` STRING
) WITH (
  'connector' = 'print'
);

INSERT INTO print_sink_1 SELECT * FROM kafka_1;

